import React, { useEffect, useState } from 'react'
import '../customcss.css';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap'
import { useParams } from 'react-router-dom';
import Home from '../pages/Home';

export default function EditUser({showEditModal, hideEditModal, EditIsOpen, selectedId, sid},props) {
  const [user1, setUser] = useState(null);

  useEffect(() => {
      // Here you can fetch the user data for the given userId from the API or any other data source
      // and store it in the state variable `user`
      if (props.userId) {
          // Fetch the user data for the given userId and update the state variable `user`
          const fetchedUser = { id: props.userId, name: "John Doe" };
          setUser(fetchedUser);
      }
  }, [props.userId]);

    const [users,setUsers]= useState([])
    
    const {id}=useParams

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');

    const user = {
        name,
        email,
    };

    useEffect(()=> {
        loadAUser();
    }, []);

    const onSubmit =async (e) => {
        e.preventDefault();
        await axios.put(`http://localhost:8080/api/updateEmployee/${id}`, user);
        // setUser('');
        setName('');
        setEmail('');
        // hideAddModal();

    };

    const loadAUser=async()=>{
        const result = await axios.get("http://localhost:8080/api/getEmployee/${id}");
        setUsers(result.data);

    }

    console.log(selecteduser.id);

  return (
    <>

<Modal show={EditIsOpen} onHide={hideEditModal}>
  
    <Modal.Header closeButton>
      <Modal.Title>Edit Employee </Modal.Title>
      </Modal.Header>
      <Form>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Address</Form.Label>
            <Form.Control as="textarea" rows={3} required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Phone</Form.Label>
            <Form.Control type="text" required />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={hideEditModal}>
            Cancel
          </Button>
          <Button variant="primary" type="submit">
            Save
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>
      
    </>
  )
}
