import React, { useEffect, useState } from 'react'
import '../customcss.css';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap'
import { useParams } from 'react-router-dom';

export default function EditUser({showEditModal, hideEditModal, EditIsOpen}) {
  

    const [users,setUsers]= useState([])
    
    const {id}=useParams

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');

    const user = {
        name,
        email,
    };

    useEffect(()=> {
        loadAUser();
    }, []);

    const onSubmit =async (e) => {
        e.preventDefault();
        await axios.put(`http://localhost:8080/api/updateEmployee/${id}`, user);
        // setUser('');
        setName('');
        setEmail('');
        // hideAddModal();

    };

    const loadAUser=async()=>{
        const result = await axios.get("http://localhost:8080/api/getEmployee/${id}");
        setUsers(result.data);

    }
    

  return (
    <>

<Modal show={EditIsOpen} onHide={hideEditModal}>
  <h1></h1>
      <Modal.Header closeButton>
        <Modal.Title>Edit Employee</Modal.Title>
      </Modal.Header>
      <Form>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Address</Form.Label>
            <Form.Control as="textarea" rows={3} required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Phone</Form.Label>
            <Form.Control type="text" required />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={hideEditModal}>
            Cancel
          </Button>
          <Button variant="primary" type="submit">
            Save
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>
      
    </>
  )
}
