import React, { useEffect, useState } from 'react'
import '../customcss.css';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap'

export default function Home() {

  
    const [users,setUsers]= useState([])

    // self running running function
    useEffect(()=> {
        loadUsers();
    }, []);

    const loadUsers=async()=>{
        const result = await axios.get("http://localhost:8080/api/getEmployees");
        setUsers(result.data);

    // add components
    const [isOpen, setIsOpen] = React.useState(false);

    
    const showModal = () => {
        setIsOpen(true);
    };

    const hideModal = () => {
        setIsOpen(false);
    };
    
    }

    return (
        <>
    {/* Add modal */}
      <Button variant="primary" onClick={showModal}>
        Edit Employee
      </Button>

      
      <Modal show={isOpen} onHide={hideModal}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Employee</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control type="text" placeholder="Enter name" required />
            </Form.Group>

            <Form.Group>
              <Form.Label>Email</Form.Label>
              <Form.Control type="email" placeholder="Enter email" required />
            </Form.Group>

            <Form.Group>
              <Form.Label>Address</Form.Label>
              <Form.Control as="textarea" placeholder="Enter address" required />
            </Form.Group>

            <Form.Group>
              <Form.Label>Phone</Form.Label>
              <Form.Control type="text" placeholder="Enter phone number" required />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={hideModal}>
            Cancel
          </Button>
          <Button variant="primary" type="submit">
            Save
          </Button>
        </Modal.Footer>
      </Modal>
    
        

        </>



        
    )
}
