import React, { useEffect, useState } from 'react'
import '../customcss.css';
import axios from 'axios';
import AddUser from '../users/AddUser';
 
export default function Home() {

    useEffect(() => {
        // Activate tooltip
        $('[data-toggle="tooltip"]').tooltip();
    
        // Select/Deselect checkboxes
        var checkbox = $('table tbody input[type="checkbox"]');
        $("#selectAll").click(function () {
          if (this.checked) {
            checkbox.each(function () {
              this.checked = true;
            });
          } else {
            checkbox.each(function () {
              this.checked = false;
            });
          }
        });
        checkbox.click(function () {
          if (!this.checked) {
            $("#selectAll").prop("checked", false);
          }
        });
      }, []);



    const [users,setUsers]= useState([])

    
    // self running running function
    useEffect(()=> {
        loadUsers();
    }, []);

    const loadUsers=async()=>{
        const result = await axios.get("http://localhost:8080/api/getEmployees");
        setUsers(result.data);
    }

    

    return (
        <div className="container">
            <div className="table-wrapper">
                <div className="table-title">
                    <div className="row">
                        <div className="col-sm-6">
                        <h2>Manage <b>Employees</b></h2>
                        </div>
                        <div className="col-sm-6">
                        <a href="#addEmployeeModal" className="btn btn-success" data-toggle="modal"><i className="material-icons">&#xE147;</i> <span>Add New Employee</span></a>
                        <a href="#deleteEmployeeModal" className="btn btn-danger" data-toggle="modal"><i className="material-icons">&#xE15C;</i> <span>Delete</span></a>
                        </div>
                    </div>
                </div>
                <table className="table table-striped table-hover">
                    <thead>
                        <tr>
                        <th>
                            <span className="custom-checkbox">
                            <input type="checkbox" id="selectAll"/>
                            <label for="selectAll"></label>
                            </span>
                        </th>
                        <th></th>
                        <th>Id</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user,index)=>(
                            <tr>
                                <th>
                                    <span className="custom-checkbox">
                                    <input type="checkbox" id="checkbox1" name="options[]" value="1"/>
                                    <label for="checkbox1"></label>
                                    </span>
                                </th>
                                <td scope='row' key={index}>{index+1}</td>
                                <td>{user.id}</td>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>
                                    <a href="#viewEmployeeModal" className="view" data-toggle="modal"><i className="material-icons" data-toggle="tooltip" title="view">&#xE8F4;</i></a>
                                    <a href="#editEmployeeModal" className="edit" data-toggle="modal"><i className="material-icons" data-toggle="tooltip" title="Edit">&#xE254;</i></a>
                                    <a href="#deleteEmployeeModal" className="delete" data-toggle="modal"><i className="material-icons" data-toggle="tooltip" title="Delete">&#xE872;</i></a>
                                </td>
                            </tr>
                        ))}
                        
                    </tbody>
                </table>
                <div className="clearfix">
                    <div className="hint-text">Showing <b>5</b> out of <b>25</b> entries</div>
                    <ul className="pagination">
                        <li className="page-item disabled"><a href="#">Previous</a></li>
                        <li className="page-item"><a href="#" className="page-link">1</a></li>
                        <li className="page-item"><a href="#" className="page-link">2</a></li>
                        <li className="page-item active"><a href="#" className="page-link">3</a></li>
                        <li className="page-item"><a href="#" className="page-link">4</a></li>
                        <li className="page-item"><a href="#" className="page-link">5</a></li>
                        <li className="page-item"><a href="#" className="page-link">Next</a></li>
                    </ul>
                </div>
            </div>      
        </div>

        



        
    )
}
