import React, { useEffect, useState } from 'react'
import '../customcss.css';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap'

export default function DeleteUser(props) {
    //-------------------------------------------------------------------
    

    const [auser, setUser] = useState(null);

    useEffect(() => {
      co
        // Here you can fetch the user data for the given userId from the API or any other data source
        // and store it in the state variable `user`
        if (props.userId) {
            // Fetch the user data for the given userId and update the state variable `user`
            const fetchedUser = { id: props.userId, name: props.userName, email: props.userEmail};
            setUser(fetchedUser);
        }
    }, [props.userId]);

    //--------------------------------------------------------------------

    const [users,setUsers]= useState([])
    
    const [name, setName] = useState(props.userName);
    const [email, setEmail] = useState(props.userEmail);

    const user = {
        name,
        email,
    };


    const onSubmit =async (e) => {
        e.preventDefault();
        await axios.put(`http://localhost:8080/api/deleteEmployee/${auser && (auser.id)}`, user);
        // setUser('');
        setName('');
        setEmail('');
        props.loadUsers();
        props.onClose();

    };


  return (
    <>
  <Modal show={props.isOpen} onHide={props.onClose}>  
      <Modal.Header closeButton>
        <Modal.Title>Delete Employee</Modal.Title>
      </Modal.Header>
      <Form onSubmit={onSubmit}>
      {auser && (
      <Modal.Body>
        <p>Are you sure you want to delete these Records?</p>
        <p className="text-warning"><small>This action cannot be undone.</small></p>
      </Modal.Body>
      )}
      </Form>
      <Modal.Footer>
        <Button variant="secondary" onClick={props.onClose}>
          Cancel
        </Button>
        <Button variant="danger" type="submit">
          Delete
        </Button>
      </Modal.Footer>
    </Modal>
      
    </>
  )
}
