import React, { useEffect, useState } from 'react'
import '../customcss.css';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap'

export default function ViewUser(props) {

   //-------------------------------------------------------------------
    

   const [auser, setUser] = useState(null);

   useEffect(() => {
       // Here you can fetch the user data for the given userId from the API or any other data source
       // and store it in the state variable `user`
       if (props.userId) {
           // Fetch the user data for the given userId and update the state variable `user`
           const fetchedUser = { id: props.userId, name: props.userName, email: props.userEmail};
           setUser(fetchedUser);
       }
   }, [props.userId]);

   //--------------------------------------------------------------------

   const [users,setUsers]= useState([])
   
   const {id}=useParams()

   const [name, setName] = useState(props.userName);
   const [email, setEmail] = useState(props.userEmail);

   const user = {
       name,
       email,
   };

   // useEffect(()=> {
   //     loadAUser();
   // }, []);

   const onSubmit =async (e) => {
       e.preventDefault();
       await axios.put(`http://localhost:8080/api/updateEmployee/${auser && (auser.id)}`, user);
       // setUser('');
       setName('');
       setEmail('');
       props.loadUsers();
       props.onClose();

   };

   console.log(auser && (auser.id));

  return (
    <div>
      <Modal id="viewEmployeeModal" show={ViewIsOpen} onHide={hideViewModal}>
      <Modal.Header closeButton>
        <Modal.Title>View Employee</Modal.Title>
      </Modal.Header>
      <Form>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Address</Form.Label>
            <Form.Control as="textarea" rows={3} required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Phone</Form.Label>
            <Form.Control type="text" required />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          {/* <Button variant="secondary" onClick={hideEditModal}>
            Cancel
          </Button>
          <Button variant="primary" type="submit">
            Save
          </Button> */}
        </Modal.Footer>
      </Form>
    </Modal>
    </div>
  )
}
