import React, { useEffect, useState } from 'react'
import '../customcss.css';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap'
import AddUser from '../users/AddUser';
import DeleteUser from '../users/DeleteUser';

export default function Home() {
    // add employee
    const [AddIsOpen, setAddOpen] = React.useState(false);

    const showAddModal = () => {
        setAddOpen(true);
    };
    const hideAddModal = () => {
        setAddOpen(false);
    };

    const[user, setUser]=useState({
        name:"",
        email:""
    })

    const{name,email}=user

    const onInputChange=(e)=>{
        setUser({...user, [e.target.name]:e.target.value})
    }

    const onSubmit =async (e) => {
        e.preventDefault();
        await axios.post("http://localhost:8080/api/addEmployee", user);
        hideAddModal();
        loadUsers();

    };

    //End add employee


    

    const [EditIsOpen, setEditOpen] = React.useState(false);
  
    const [ViewIsOpen, setViewOpen] = React.useState(false);

    
    


    const showEditModal = () => {
        setEditOpen(true);
      };
    const hideEditModal = () => {
        setEditOpen(false);
    }

    

    const showViewModal = () => {
        setViewOpen(true);
      };
    const hideViewModal = () => {
        setViewOpen(false);
    }


    const [isOpen, setIsOpen] = React.useState(false);

    const showModal = () => {
        setIsOpen(true);
    };

    const hideModal = () => {
        console.log("test");
        setIsOpen(false);
    };
    




    const [users,setUsers]= useState([])

    // self running running function
    useEffect(()=> {
        loadUsers();
    }, []);

    const loadUsers=async()=>{
        const result = await axios.get("http://localhost:8080/api/getEmployees");
        setUsers(result.data);

    
    
    }

    return (
        <>

    {/* Add modal */}
    <AddUser isOpen={isOpen} onHide={hideModal}/>

      {/* Edit employee */}
      <Modal id="editEmployeeModal" show={EditIsOpen} onHide={hideEditModal}>
      <Modal.Header closeButton>
        <Modal.Title>Edit Employee</Modal.Title>
      </Modal.Header>
      <Form>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Address</Form.Label>
            <Form.Control as="textarea" rows={3} required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Phone</Form.Label>
            <Form.Control type="text" required />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={hideEditModal}>
            Cancel
          </Button>
          <Button variant="primary" type="submit">
            Save
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>

    {/* delete modal */}
    

    {/* view employee */}
    <Modal id="viewEmployeeModal" show={ViewIsOpen} onHide={hideViewModal}>
      <Modal.Header closeButton>
        <Modal.Title>View Employee</Modal.Title>
      </Modal.Header>
      <Form>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Address</Form.Label>
            <Form.Control as="textarea" rows={3} required />
          </Form.Group>
          <Form.Group>
            <Form.Label>Phone</Form.Label>
            <Form.Control type="text" required />
          </Form.Group>
        </Modal.Body>
        {/* <Modal.Footer>
          <Button variant="secondary" onClick={hideEditModal}>
            Cancel
          </Button>
          <Button variant="primary" type="submit">
            Save
          </Button>
        </Modal.Footer> */}
      </Form>
    </Modal>
    
        <div className="container">
            <div className="table-wrapper">
                <div className="table-title">
                    <div className="row">
                        <div className="col-sm-6">
                        <h2>Manage <b>Employees</b></h2>
                        
                        </div>
                        <div className="col-sm-6">
                        <Button className="btn btn-success" onClick={showModal}><i className="material-icons">&#xE147;</i> <span>Add New Employee</span></Button>
                        <Button className="btn btn-danger"><i className="material-icons">&#xE15C;</i> <span>Delete</span></Button>
                        </div>
                    </div>
                </div>
                <table className="table table-striped table-hover">
                    <thead>
                        <tr>
                        <th>
                            <span className="custom-checkbox">
                            <input type="checkbox" id="selectAll"/>
                            <label for="selectAll"></label>
                            </span>
                        </th>
                        <th></th>
                        <th>Id</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user,index)=>(
                            <tr>
                                <th>
                                    <span className="custom-checkbox">
                                    <input type="checkbox" id="checkbox1" name="options[]" value="1"/>
                                    <label for="checkbox1"></label>
                                    </span>
                                </th>
                                <td scope='row' key={index}>{index+1}</td>
                                <td>{user.id}</td>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>
                                    <a href="#" onClick={showViewModal} className="view" ><i className="material-icons" data-toggle="tooltip" title="view">&#xE8F4;</i></a>
                                    <a href="#" onClick={showEditModal} className="edit" data-toggle="modal"><i className="material-icons" data-toggle="tooltip" title="Edit">&#xE254;</i></a>
                                    <a href="#"  className="delete" data-toggle="modal"><i className="material-icons" data-toggle="tooltip" title="Delete">&#xE872;</i></a>
                                </td>
                            </tr>
                        ))}
                        
                    </tbody>
                </table>
                <div className="clearfix">
                    <div className="hint-text">Showing <b>5</b> out of <b>25</b> entries</div>
                    <ul className="pagination">
                        <li className="page-item disabled"><a href="#">Previous</a></li>
                        <li className="page-item"><a href="#" className="page-link">1</a></li>
                        <li className="page-item"><a href="#" className="page-link">2</a></li>
                        <li className="page-item active"><a href="#" className="page-link">3</a></li>
                        <li className="page-item"><a href="#" className="page-link">4</a></li>
                        <li className="page-item"><a href="#" className="page-link">5</a></li>
                        <li className="page-item"><a href="#" className="page-link">Next</a></li>
                    </ul>
                </div>
            </div>      
        </div>

        </>



        
    )
}
